import { useState } from 'react';

function Square({ value, onSquareClick, parity }) {
  let color = parity ? "light" : "dark";
  return (
    <button className={"square " + color} onClick={onSquareClick}>
      <img src="/images/b_1.png"/>
    </button>
  );
}

function Board() {
  let board = [];
  let row;
  for (let i = 0; i < 8; i++) {
    row = [];
    for (let j = 0; j < 8; j++) {
      row.push(<Square value={1} onSquareClick={() => handleClick(0)} parity={(i + j) % 2 == 0}/>);
    }
    board.push(<div className="board-row">{row}</div>);
  }
  return (<>{board}</>);
}

export default function Game() {
  return (
    <div className="game">
      <div className="game-board">
        <Board />
      </div>
    </div>
  );
}
