import { useState } from 'react';

function Square({ value, onSquareClick, color }) {
  return (
    <button className={"square " + color} onClick={onSquareClick}>
      <img src="/images/b_1.png"/>
    </button>
  );
}

function Board() {
  return (
    <>
      <div className="status">{status}</div>
      <div className="board-row">
        <Square value={1} onSquareClick={() => handleClick(0)} color="dark"/>
        <Square value={2} onSquareClick={() => handleClick(1)} color="light"/>
        <Square value={3} onSquareClick={() => handleClick(2)}/>
      </div>
      <div className="board-row">
        <Square value={4} onSquareClick={() => handleClick(3)}/>
        <Square value={5} onSquareClick={() => handleClick(4)}/>
        <Square value={6} onSquareClick={() => handleClick(5)}/>
      </div>
      <div className="board-row">
        <Square value={7} onSquareClick={() => handleClick(6)}/>
        <Square value={8} onSquareClick={() => handleClick(7)}/>
        <Square value={8} onSquareClick={() => handleClick(8)}/>
      </div>
    </>
  );
}

export default function Game() {
  return (
    <div className="game">
      <div className="game-board">
        <Board />
      </div>
    </div>
  );
}
